    event.preventDefault();

    try {
      const response = await axios.get(`https://api.laposte.fr/datalab/v1/adresse?q=${this.state.address}&type=${this.state.type}&limit=${this.state.limit}`, {
        headers: {
          'X-Okapi-Key': 'your_api_key_here',
          'Accept': 'application/json'
        }
      });

      const data = response.data;
      const results = data.adresses;

      if (results.length > 0) {
        const firstResult = results[0];
        const city = firstResult.localite;
        const postcode = firstResult.codePostal;
        this.setState({ city, postcode, error: null });
      } else {
