import React from 'react';
import './Panier.css';

function Panier({panier, onRemoveFromCart}) {
  const total = panier.reduce((acc, curr) => acc + curr.prix, 0);
  
  return (
    <div className="Panier">
      <h2>Mon panier</h2>
      <ul>
        {panier.map((item) => (
          <li key={item.id}>
            {item.titre} ({item.prix}€)
            <button onClick={() => onRemoveFromCart(item)}>Supprimer</button>
          </li>
        ))}
      </ul>
      <div className="total">Total: {total}€</div>
    </div>
  );
}

export default Panier;
