import React, { useState } from 'react';
import './IcoPanier.css';
import icoPanier from './ico/icoPanier.png';



function IcoPanier({panier} ) {

  function totalArticle(panier){
    const totalArticle = panier.reduce(
		(acc, article) => acc +  parseInt(article.nbr)  ,
		0
	)
    return(totalArticle);

  }


  function createCustomCursor() {
    let cursor = document.getElementById('myCustomCursor');

     if (cursor) {
         console.log('myCustomCursor already exist');
         //addCursorSpecialEffectToAllPageLinks(cursor);
     } else {
         cursor = document.createElement("div");
         cursor.setAttribute("id", "myCustomCursor");
         document.body.appendChild(cursor);

         initCustomCursor(cursor);
         //addCursorSpecialEffectToAllPageLinks(cursor);
     }
 }

 
 function initCustomCursor(cursor) {
     document.body.onmousemove = function(e) {
         cursor.style.setProperty('--x', (e.clientX) + 'px');
         cursor.style.setProperty('--y', (e.clientY) + 'px');
     }
 }

 function addCursorSpecialEffectToAllPageLinks(cursor) {
     var links = document.querySelectorAll("a");

 // This for loop is used to find all the page links and add the "myCursorHoverState" css class to create special effect on hover
   for (var i = 0; i < links.length; i++) {
       var link = links[i].getAttribute("href");
       console.log(link);

       links[i].addEventListener("mouseenter", function(event) {
             console.log('In');
             cursor.classList.add("myCursorHoverState");
       }, false);

       links[i].addEventListener("mouseleave", function(event) {
             console.log('Out');
             cursor.classList.remove("myCursorHoverState");
       }, false);
   }
}

createCustomCursor();

  return (
    <div className="div_icopanier">

          
          {panier.length>0 && <span>

            <span className='fond_nbrPanier'></span>
            <span className='nbrPanier'> {totalArticle(panier)}</span>

            <img src={icoPanier} className='iconePanier'></img>



          </span>}
    </div>
  );
}

export default IcoPanier;
