import './Accueil.css'
import LocomotiveScroll from 'locomotive-scroll';
import React, { useRef, useEffect } from 'react';
function Accueil() {

    const scrollContainer = useRef(null);

    useEffect(() => {
        const scroll = new LocomotiveScroll({
            el: scrollContainer.current,
            smooth: true,
            direction: 'horizontal',
            // options de vitesse pour chaque élément de texte
            getSpeed: () => {
                return window.innerWidth > 768 ? parseFloat(scrollContainer.current.dataset.speed) : 1;
            },
        });
    }, []);
    
    return(



    <div className='div_b2b_records' >
        <span className='span_b2b'>
            <span className='titre_b2b'>BICRAVEURS2BASS</span>
            <span className='titre_b2b titre_b2b--contour'>BICRAVEURS2BASS</span>
            <span className='titre_b2b titre_b2b--contour'>BICRAVEURS2BASS</span>
        </span>

        <span className='span_records'>
            <span className='titre_b2b'>RECORDS</span>
            <span className='titre_b2b titre_b2b--contour'>RECORDS</span>
            <span className='titre_b2b titre_b2b--contour'>RECORDS</span>
        </span>

    </div>

    )
}

export default Accueil;
