import React, { useState } from 'react';
import './App.css';

import Afficher_sons from './Sons/Afficher_sons.js'; 
import Panier from './Panier/Panier.js'; 



function App() {

  const [panier, setPanier] = useState([]);

  const ajouterAuPanier = (article) => {
      setPanier([...panier, article]);
  };


  return (
    <div className="App">
    <Panier articles={panier}>
    </Panier>
    <Afficher_sons ajouterAuPanier={ajouterAuPanier}>
    </Afficher_sons>
</div>
  );
}

export default App;
