import React, { useState } from 'react';
import './Panier.css';




function Panier({ panier, updatePanier }) {


	const totalMontant = panier.reduce(
		(acc, article) => acc + article.nbr * article.prix,
		0
	)

    const totalArticle = panier.reduce(
		(acc, article) => acc + article.nbr  ,
		0
	)


function supprimerDuPanier(titre, nbr){ 
  const nouveauPanier = panier.map((article) => {
    if (article.titre === titre) {
      return {
        ...article,
        nbr:0,
        prix: article.prix
      };
    }
    return article;
  });
  updatePanier(nouveauPanier);
  console.log(nouveauPanier);
}


function viderPanier(){ 
  updatePanier([]);
}


function  modifierValue(titre, nbr, value, type){
  const nouveauPanier = panier.map((article) => {
    console.log(article);
    if (article.titre === titre) {
      if (article.type === "digital"){

        if(value == 2){

          if (article.nbr === 1){    
            alert("ne pas ajouter plusieurs sons digitaux")
            return {
              ...article,
              nbr:1,
              prix: article.prix
            };
          }
        }
        else{
          return {
            ...article,
            nbr:0,
            prix: article.prix
          };

        }

      }

      else{

        return {
          ...article,
          nbr:value,
          prix: article.prix

        }
      }

    
    }
    return article;
  
  });
  updatePanier(nouveauPanier);
}



    return (
        <div className="container_panier">

          {panier.map((article, index) => (
            <div key={index}>
              {article.nbr>0 && 
              <div>
              <span>{article.titre}</span>
              <span>{article.prix}</span>
              <span> {article.nbr}</span>
              {/* <h2>{article.type}</h2> */}
              <button onClick={() => supprimerDuPanier(article.titre, article.nbr)}>Supprimer du Panier</button>
              <input
          type="number"
        pattern="[0-9]*"
        value={article.nbr}
        onChange={e => 
        
        modifierValue(article.titre, article.nbr, e.target.value, article.type)
        
        
        }
/>

              </div>
              
              }
              
              
              
              


            </div>
            
          ))}
          {totalMontant>0 && <h1>Total:{totalMontant} â‚¬</h1>}
          {totalMontant>0 && <h1>Total articles:{totalArticle} </h1>}
          <button onClick={()=>viderPanier()}> Vider le panier</button>

                       
        </div>
      );
    
}

export default Panier;
