import './Accueil.css'
import useLocoScroll from './hooks/useLocoScroll.js';
import Rotate from 'react-reveal';
import logo_rond from '../global/Nav/images/logo_rond.png';


import React, { useRef, useEffect, useState } from 'react';

function Accueil() {
    const [preloader, setPreloader] = useState(true);

    useLocoScroll(!preloader);


    const [timer, setTimer] = useState(1);
    const id = useRef(null);

    const clear = ()=>{
        window.clearInterval(id.current);
        setPreloader(false);
    }

    useEffect(()=>{
        id.current = window.setInterval(()=>{
            setTimer((timer)=> timer-1)

        },1000 )

    }, [])

    useEffect(()=>{
        if(timer ===0){
            clear();
        }

    }, [timer])

    if(window.location.pathname !== '/') {
        document.querySelector('html').classList.remove('is-scrollable');
      }

    return(

        <span>
            {preloader?(
            
            <div className='loader'>
                <h1> chargement</h1>

            </div>)
            :(

            <div id="main-container" className="main-container" data-scroll-container> 
                <div className='div_b2b_records' data-scroll-section id="sec1">
                    <span className='span_b2b' >
                        <span className='titre_b2b' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                    </span>
                    <span className='span_records' >
                        <span className='titre_b2b' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                    </span>
                </div>



 
                <div className='section2' data-scroll-section id="sec2" >
                        <h1 className='label_ind blanc'  data-scroll-position="left" data-scroll data-scroll-direction="vertical" data-scroll-speed="-3">LABEL INDE</h1>      
                        <h1 className='label_ind'  data-scroll-position="left" data-scroll data-scroll-direction="vertical" data-scroll-speed="-1">NORMAND</h1>
                </div>
                
                <div className='section3' data-scroll-section id="sec3" >

                       <img className='logo_rond' data-scroll-position="left" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-3" src={logo_rond}></img>
                </div>

            </div>
            )}
        </span>

    )
}

export default Accueil;
