import React, { useState } from 'react';
import ReactPlayer from 'react-player/soundcloud'

import './Player.css';


let Hauteur = window.innerWidth;



function Player({ url , isPlaying, setIsPlaying}) { 
  

    if(isPlaying){


      return (
        <div className="div_player">
            <ReactPlayer 
            show_artwork="false"
            show_playcount="false"
            className="player"
            url={url}   
            playing={true} 
            width="100vw"
            height="100vh"
            onPause={() => setIsPlaying(false)  }
            />
        </div>
      );

}    } 


export default Player;
