import React, { useState } from 'react';
import ReactPlayer from 'react-player/soundcloud'

import './Player.css';




function Player({ url , isPlaying, setIsPlaying}) { 
  let largeurAppareil = window.innerWidth;
  if (largeurAppareil < 784 ) {
    let heightPlayer = '20vw';
  }
  else{
    let heightPlayer = '10vw';
  }


    if(isPlaying){


      return (
        <div className="div_player">
            <ReactPlayer 
            className="player"
            url={url}   
            playing={true} 
            width="100vw"
            height={heightPlayer}
            onPause={() => setIsPlaying(false)  }
            />
        </div>
      );

}    } 


export default Player;
