import React, { useEffect } from 'react';
import { createPortal } from 'react-dom';

const Paiement = () => {
  useEffect(() => {
    const checkoutId = '2ceffb63-cbbe-4227-87cf-0409dd191a98';

    const newWindow = window.open('', '_blank');
    const newDocument = newWindow.document;

    const script = newDocument.createElement('script');
    script.type = 'text/javascript';
    script.src = 'https://gateway.sumup.com/gateway/ecom/card/v2/sdk.js';

    newDocument.head.appendChild(script);

    script.onload = () => {
      const sumupCardContainer = newDocument.createElement('div');
      sumupCardContainer.id = 'sumup-card';
      newDocument.body.appendChild(sumupCardContainer);

      SumUpCard.mount({
        id: 'sumup-card',
        checkoutId,
        onResponse: (type, body) => {
          console.log('Type', type);
          console.log('Body', body);
        },
      });
    };

    return () => {
      newWindow.close();
    };
  }, []);

  return createPortal(null, document.getElementById('sumup-card'));
};

export default Paiement;
