import React, { useState } from 'react';

function Formulaire() {
  const [state, setState] = useState({
    numero_rue: '',
    type_voie: '',
    nom_voie: '',
    code_postal: '',
    ville: '',
    error: null
  });

  const handleSubmit = async event => {
    event.preventDefault();
    // Envoyer la requête à l'API de La Poste ici
  };

  const handleChange = event => {
    setState({ ...state, [event.target.name]: event.target.value });
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <label>
        Numéro de rue:
        <input type="text" name="numero_rue" value={state.numero_rue} onChange={handleChange} />
      </label>
      <label>
        Type de voie:
        <input type="text" name="type_voie" value={state.type_voie} onChange={handleChange} />
      </label>
      <label>
        Nom de voie:
        <input type="text" name="nom_voie" value={state.nom_voie} onChange={handleChange} />
      </label>
      <label>
        Code postal:
        <input type="text" name="code_postal" value={state.code_postal} onChange={handleChange} />
      </label>
      <label>
        Ville:
        <input type="text" name="ville" value={state.ville} onChange={handleChange} />
      </label>
      <button type="submit">Rechercher</button>
    </form>
    </div>
  );
}

export default Formulaire;
