import React, { useState } from 'react';
import './Panier.css';




function Panier({ panier, updatePanier }) {


	const totalMontant = panier.reduce(
		(acc, article) => acc + article.nbr * article.prix,
		0
	)

    const totalArticle = panier.reduce(
		(acc, article) => acc + article.nbr,
		0
	)


function supprimerDuPanier(titre){ 
    const existeDeja = panier.find((article) => article.titre === titre);
    if(existeDeja){

      const listeFiltree = panier.filter(
				(article) => article.titre !== titre
			)

        updatePanier([
				  ...listeFiltree,
				  { titre, nbr: existeDeja.nbr - 1 }])
    }
    else {
      let listeFiltree = panier.filter(o => o.nbr !== 0);



      console.log(listeFiltree)
      updatePanier([
        ...listeFiltree,
        { titre, nbr: 0 }])
		}

  }


    return (
        <div className="container_panier">
          {panier.map((article, index) => (
            <div key={index}>
              {article.titre}  {article.prix}€    Quantité {article.nbr}

              <h2>{article.type}</h2>
              <button onClick={() => supprimerDuPanier(article.titre)}>Supprimer du Panier</button>
            </div>
            
          ))}
          {totalMontant>0 && <h1>Total:{totalMontant} €</h1>}
          {totalMontant>0 && <h1>Total articles:{totalArticle} </h1>}

                       
        </div>
      );
    
}

export default Panier;
