import React from 'react';
import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

function Formulaire() {
    // form validation rules 
    const validationSchema = Yup.object().shape({
        email: Yup.string()
            .required('Merci de renseigner un email')
            .email("Votre email n'est pas valide"),

        nom: Yup.string()
            .string()
            .matches(/^[a-zA-ZÀ-ÖÙ-öù-ÿĀ-žḀ-ỿ0-9\s\-\/.]+$/, 'Please enter valid name')
            .max(40)
            .required(),

        
    });
    const formOptions = { resolver: yupResolver(validationSchema) };


    const { register, handleSubmit, reset, formState } = useForm(formOptions);
    const { errors } = formState;



    function onSubmit(data) {
        alert('SUCCESS!! :-)\n\n' + JSON.stringify(data, null, 4));
        return false;
    }

    return (
        <div className="card m-3">
            <h5 className="card-header">React Hook Form 7 - Email Validation Example</h5>
            <div className="card-body">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="row">
                        <div className="col mb-3">
                            <label class="form-label">Email</label>
                            <input name="email" type="text" {...register('email')} className={`form-control ${errors.email ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.email?.message}</div>
                        </div>
                        <div className="col mb-3">
                            <label class="form-label">Nom</label>
                            <input name="Nom" type="text" {...register('email')} className={`form-control ${errors.nom ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.nom?.message}</div>
                        </div>

                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-primary me-1">Submit</button>

                    </div>
                </form>
            </div>
        </div>
    )

}

export default Formulaire;
