import React, { useState } from 'react';
import './Formulaire.css';
import { useForm } from "react-hook-form";




function Formulaire({panier}) {
    const physique = panier.find(physique => physique.type !== 'digital') !== undefined;
    console.log (physique );



    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);

    console.log(watch("Nom")); 
    console.log(watch("Prénom")); 
    return (

      <form onSubmit={handleSubmit(onSubmit)}>

        <input defaultValue="Nom" {...register("Nom")} />
        <input defaultValue="Prénom" {...register("Prénom")} />


        


        {errors.exampleRequired && <span>This field is required</span>}
        
        <input type="submit" />
      </form>
    );
    
}

export default Formulaire;
