import React, { useState , useEffect, useRef} from 'react';
import './App.css';
import { Routes, Route, Router } from "react-router-dom";
import { AnimatePresence } from "framer-motion";
import { motion } from "framer-motion"
// import AfficherSons from './Sons/Afficher_sons.js'; 
import Panier from './boutique/Panier/Panier.js'; 
// import Nav from './Nav/Nav.js'; 
// import Curseur from './Curseur/Curseur.js'; 
// import Footer from './Footer/Footer.js'; 
import Formulaire from './boutique/Panier/Formulaire/Formulaire.js'; 
// import IcoPanier from './IcoPanier/IcoPanier.js'; 
// import AfficherPhysique from './AfficherPhysique/AfficherPhysique.js';

// import totalArticle from './Panier/Panier.js';
// import classChanger from './IcoPanier/IcoPanier.js';
import Mainshop from './boutique/Mainshop.js';
import Accueil from './accueil/Accueil.js';
import Musique from './musique/Musique.js';
import Events from './events/Events.js';
import Nav from './global/Nav/Nav.js'; 
import Footer from './global/Footer/Footer.js'; 
import ScrollToTop from './global/ScrollToTop.js';
import useLocoScroll from './hooks/useLocoScroll.js';


function Layout(props) {
  return (
    <>
      <Nav/>
      {props.children}
      <Footer />
    </>
  );
}






function App() {

  const [preloader, setPreloader] = useState(true);










  useLocoScroll(!preloader);


  const [timer, setTimer] = useState(1);
  const id = useRef(null);

  const clear = ()=>{
      window.clearInterval(id.current);
      setPreloader(false);
  }

  useEffect(()=>{
      id.current = window.setInterval(()=>{
          setTimer((timer)=> timer-1)

      },1000 )

  }, [])

  useEffect(()=>{
      if(timer ===0){
          clear();
      }

  }, [timer])



  return (


    <div className="App">
        {/* <Nav/> */}
        <meta name="viewport" content="width=device-width, initial-scale=1.0, shrink-to-fit=no"></meta>


          <Layout>
          <AnimatePresence mode='wait'>
          <ScrollToTop/>

          <Routes>

            <Route path="/" element={ <Accueil />}/> 
            <Route path="/shop" element={ <Mainshop />}/> 
            <Route path='/musique' element={ <Musique />}/>
            <Route path='/events' element={ <Events />}/>
            <Route element={<h1>NOT FOUND</h1>} />
          </Routes>  

          </AnimatePresence>
          </Layout>



        {/* <Footer/>   */}

    </div>

  );
}

export default App;
// export { totalArticle };
