import React, { useState } from 'react';
import './App.css';

import AfficherSons from './Sons/Afficher_sons.js'; 
import Panier from './Panier/Panier.js'; 
import Nav from './Nav/Nav.js'; 
import Player from './Player/Player.js'; 


function App() {
  const [panier, updatePanier] = useState([]);
  const [player, updatePlayer] = useState('');
  const [isPlaying, setIsPlaying] = useState(false);

  return (
    <div className="App">
      <Nav />
      <Panier panier={panier} updatePanier={updatePanier} />
      <AfficherSons
        panier={panier}
        updatePanier={updatePanier}
        updatePlayer={updatePlayer}
        setIsPlaying={setIsPlaying}
      />
      <Player url={player} isPlaying={isPlaying} />
    </div>
  );
}

export default App;
