import React, { useState } from 'react';
import * as yup from 'yup';

const schema = yup.object().shape({
  firstname: yup.string().notOneOf(["firstname", "unknown", "first_name", "anonyme", "user", "admin", "name", "nom", "prénom", "test"])
    .min(2, 'Le prénom doit comporter au moins 2 caractères')
    .max(50, 'Le prénom doit comporter moins de 50 caractères')
    .matches(/^[a-zA-Z'çàâéèêëîïôöùûü]+(?:-[a-zA-Z'çàâéèêëîïôöùûü]+)*$/, 'Le prénom ne doit pas contenir de chiffres, de caractères spéciaux, de voyelles ni de répétitions de plus de 2 caractères'),
  lastname: yup.string().notOneOf(["lastname", "unknown", "first_name", "anonyme", "user", "admin", "name", "nom", "prénom", "test"])
    .min(2, 'Le nom doit comporter au moins 2 caractères')
    .max(50, 'Le nom doit comporter moins de 50 caractères')
    .matches(/^[a-zA-Z'çàâéèêëîïôöùûü]+(?:-[a-zA-Z'çàâéèêëîïôöùûü]+)*$/, 'Le nom ne doit pas contenir de chiffres, de caractères spéciaux, de voyelles ni de répétitions de plus de 2 caractères')
    .notOneOf([yup.ref('firstname')], 'Le nom et le prénom ne doivent pas être identiques'),
  email: yup.string().email('L’adresse email est invalide').required('L’adresse email est obligatoire'),
  birthdate: yup.date().max(new Date(new Date().getFullYear() - 18, 12, 31), 'Vous devez être majeur pour acheter sur ce site'),
});

function Formulaire()  {
  const [formValues, setFormValues] = useState({
    firstname: '',
    lastname: '',
    email: '',
    birthdate: '',
  });

  const [formErrors, setFormErrors] = useState({
    firstname: '',
    lastname: '',
    email: '',
    birthdate: '',
  });

  const handleChange = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    schema.validate(formValues, { abortEarly: false })
      .then(() => {
        console.log('Validation réussie');
        // Envoi des données du formulaire au serveur
      })
      .catch((errors) => {
        const newErrors = {};
        errors.inner.forEach((error) => {
          newErrors[error.path] = error.message;
        });
        setFormErrors(newErrors);
      });
  };
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="firstname">Prénom :</label>
        <input type="text" id="firstname" name="firstname" value={formValues.firstname} onChange={handleChange} />
        {formErrors.firstname && <p>{formErrors.firstname}</p>}
      </div>
      <div>
    <label htmlFor="lastname">Nom :</label>
    <input type="text" id="lastname" name="lastname" value={formValues.lastname} onChange={handleChange} />
    {formErrors.lastname && <p>{formErrors.lastname}</p>}
  </div>
  <div>
    <label htmlFor="email">Email :</label>
    <input type="email" id="email" name="email" value={formValues.email} onChange={handleChange} />
    {formErrors.email && <p>{formErrors.email}</p>}
  </div>
  <div>
    <label htmlFor="birthdate">Date de naissance :</label>
    <input type="date" id="birthdate" name="birthdate" value={formValues.birthdate} onChange={handleChange} />
    {formErrors.birthdate && <p>{formErrors.birthdate}</p>}
  </div>
  <button type="submit">Envoyer</button>
</form>
);
};
export default Formulaire;
