import { CardElement, injectStripe } from 'react-stripe-elements';
import React from 'react';


class PaiementForm extends React.Component {
  handleSubmit = async (event) => {
    event.preventDefault();

    const { token } = await this.props.stripe.createToken();
    
    if (token) {
      // Envoyez le token au backend pour finaliser le paiement
      const response = await fetch('/paiement', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: token.id }),
      });

      if (response.ok) {
        // Redirigez l'utilisateur vers la page de succès du paiement
        window.location.href = '/paiement/succes';
      } else {
        // Gérez les erreurs de paiement
        console.error('Erreur de paiement');
      }
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <CardElement />
        <button type="submit">Payer</button>
      </form>
    );
  }
}

export default injectStripe(PaiementForm);
