import React, { useState } from 'react';
import ReactPlayer from 'react-player/soundcloud'

import './Player.css';





function Player({ url , isPlaying, setIsPlaying}) { 
    if(isPlaying){
      const element = document.getElementById('panier')

      element.classList.remove('classname'); // reset animation
      element.classList.add('player--haut');
      void element.offsetWidth; // trigger reflow  

      return (
        <div className="div_player">
            <ReactPlayer 
            className="player"
            url={url}   
            playing={true} 
            width="100vw" 
            height="10vw"
            onPause={() => setIsPlaying(false)  }
            />
        </div>
      );
  } 
}


export default Player;
