import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import './Formulaire.css';

const schema = yup.object().shape({
    prenom: yup.string()
    .required('Veuillez fournir votre prénom.')
    .min(2, 'Le prénom doit comporter au moins 2 caractères.')
    .matches(/[aeiouyAEIOUY]/, 'Le prénom doit contenir au moins une voyelle.')
    .notOneOf([  'firstname',
    'lastname',
    'unknown',
    'first_name',
    'last_name',
    'anonyme',
    'user',
    'admin',
    'name',
    'nom',
    'prénom',
    'test',], 'Cette valeur est interdite.')
    .matches(/^[a-zA-Zéèêàâûù:'\-ç]+$/, 'Le prénom contient des caractères non autorisés.')
    .matches(/^(?!.*(.)\1{2}).*$/, 'La saisie contient des caractères répétitifs.')
    .test('not-equal', 'Le prénom ne peut pas être identique au nom.', function(value) {
        return value !== this.parent.nom;
      }),



      nom: yup.string()
      .required('Veuillez fournir votre prénom.')
      .min(2, 'Le prénom doit comporter au moins 2 caractères.')
      .matches(/[aeiouyAEIOUY]/, 'Le prénom doit contenir au moins une voyelle.')
      .notOneOf([  'firstname',
      'lastname',
      'unknown',
      'first_name',
      'last_name',
      'anonyme',
      'user',
      'admin',
      'name',
      'nom',
      'prénom',
      'test',], 'Cette valeur est interdite.')
      .matches(/^[a-zA-Zéèêàâûù:'\-ç]+$/, 'Le prénom contient des caractères non autorisés.')
      .matches(/^(?!.*(.)\1{2}).*$/, 'La saisie contient des caractères répétitifs.')
      .test('not-equal', 'Le prénom ne peut pas être identique au nom.', function(value) {
          return value !== this.parent.prenom;
        }),


    email: yup.string()
        .required('Veuillez fournir une adresse email.')
        .email('Veuillez fournir une adresse email valide.'),

    adresse: yup.string()
        .required('Veuillez fournir une adresse.'),
    
    zip: yup.number()
        .required('Veuillez fournir un code postal.')       
        .typeError('Veuillez fournir un nombre.'),
    
    ville: yup.string()
        .required('Veuillez fournir une ville.')       
        


});

function Formulaire() {
  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div className='div_form'>

    <form className="form" onSubmit={handleSubmit(onSubmit)}>
    <h3>Vos infos</h3>
      <span className="paireForm">
      <input {...register("prenom")} placeholder="Prénom" />
      {errors.prenom && <span>{errors.prenom.message}</span>}


      <input className="paire" {...register("nom")} placeholder="Nom" />
      {errors.nom && <span>{errors.nom.message}</span>}
      </span>
      <input {...register("email")} placeholder="Email" />
      {errors.email && <span>{errors.email.message}</span>}


      <input {...register("adresse")} placeholder="Adresse" />
      {errors.adresse && <span>{errors.adresse.message}</span>}
    <span className="paireForm">
      <input {...register("zip")} placeholder="Code postal" />
      {errors.zip && <span>{errors.zip.message}</span>}

      <input className="paire" {...register("ville")} placeholder="Ville" />
      {errors.ville && <span>{errors.ville.message}</span>}
    </span>
      <select className="select" id="country" name="country">
        <option value="france">France</option>

        </select>



      <input type="submit" />
    </form>
    </div>
  );
}

export default Formulaire;
