import React, { useState } from 'react';
import './IcoPanier.css';
import icoPanier from './ico/icoPanier.png';



function IcoPanier({panier, isPlaying} ) {

  function totalArticle(panier){
    const totalArticle = panier.reduce(
		(acc, article) => acc +  parseInt(article.nbr)  ,
		0
	)
    return(totalArticle);

  }

  console.log(isPlaying);
  console.log(document.getElementById('icopanier'));

  if(isPlaying === false){
  const target = document.getElementById('icopanier');
  target.classList.remove('div_icopanier');
  target.classList.add('icopanier_top');
  }

  // else {
  //   target.classList.add('div_icopanier');
  //   target.classList.remove('icopanier_top'); 
  // }

  return (
    <div id ="icopanier" className="div_icopanier">

          
          {panier.length>0 && <span>

            <span className='fond_nbrPanier'></span>
            <span className='nbrPanier'> {totalArticle(panier)}</span>
            <img src={icoPanier} className='iconePanier'></img>



          </span>}
    </div>
  );

}

export default IcoPanier;
