import React, { useState } from 'react';
import listeSons from './listeSons.json';
import './Afficher_sons.css';
import logopanier from './icones/panier.png';
import logoplay from './icones/play.png';
import Zoom from 'react-reveal';
import { Slide } from 'react-reveal';
import TransitionGroup from 'react-transition-group/TransitionGroup';


function Afficher_sons({panier, updatePanier, updatePlayer, isPlaying, setIsPlaying}) {

  const [genreTri, setGenreTri] = useState('');
  const trierParGenre = (sons, genre) => {
    return sons.filter((son) => son.genre.includes(genre));
  };

  const listeSonsTries = genreTri ? trierParGenre(listeSons, genreTri) : listeSons;


function ajouterPanier(titre, prix, type, nbr) {
    let nouveauPanier ="";
    const existeDeja = panier.find((article) => article.titre === titre);
    if (existeDeja)  {
      console.log(type == "digial");
      if (type == "digital" && nbr > 0) {
        alert("vous ne pouvez pas ajouter plusieurs sons digitaux");
      }
      else {
        nouveauPanier = panier.map((article) => {
          if (article.titre === titre) {
            return {
              ...article,
              nbr: 1 ,
              prix: article.prix
            };
          }
          return article;
        });
        console.log(nouveauPanier);
        updatePanier(nouveauPanier);
      }
    } else {  
      const nouveauPanier = [...panier, { titre, prix , type, nbr:1}];
      console.log(nouveauPanier);
      updatePanier(nouveauPanier);
    }
  }

  // const [isPlaying, setIsPlaying] = useState();
  function playSound(url) {
 
    window.open(url);
  } 




  return (
    
    <div className="container_global">

        <Zoom left><h2 className="sons_wav ami_dj"><Slide top cascade>DJ? retrouve nos sons en wav! </Slide></h2></Zoom>
        <Zoom right cascade>
        <span className='container_dj'>
        <h3 className='wav_desc'>Fini les mp3 compressés, on vous propose tous nos sons en WAV haute qualité!</h3>
        <span className='span_pass'><h3 className='wav_desc wav_desc--2'>Eligible au pass culture? Profitez-en, ça fait marcher l'asso!</h3><a href="https://www.example.com" className="logo_pass"></a></span>
      </span>
      </Zoom>

      <div className="container_tri">
        <span>Trier par genre:</span>
        <select className='option_tri'>
        <option  onClick={() => setGenreTri('')}>Tous</option>
        <option  onClick={() => setGenreTri('Gabber')}>Gabber</option>
        <option  onClick={() => setGenreTri('Hardcore')}>Hardcore</option>
        <option  onClick={() => setGenreTri('Tribe')}>Tribe</option>
        </select>
      </div>
     
    <div className="articlesContainer">
    <TransitionGroup>
      <Zoom ssrFadeout>
      <div className="itemContainer" >
        {listeSonsTries.map((son) => (
          <div className="item">
            <img class="itemImg" src={son.img} alt={son.titre} />

            <h4 className="titre">{son.titre}</h4>
            {son.genre && <h5 className="genre">{son.genre.join(' / ')}</h5>}

            <span className="containerPrix">

            <img onClick={() => playSound(son.url)} className="icoItem  icoItem--play" src={logoplay}></img>
            <span className='item_panier_prix'>
             <span className='son--prix'>{son.prix}€ </span>


              <button className='boutonItem--panier' onClick={() => ajouterPanier(son.titre, son.prix, son.type, son.nbr)}><img className="icoItem" src={logopanier}></img></button>
            </span>
            </span>
          </div>

      ))}
    </div>
 
    </Zoom>
    </TransitionGroup>
      </div>
    
    
    </div>
    
  );
}

export default Afficher_sons;
