import React, { useState } from 'react';
import listeSons from './listeSons.json';
import './Afficher_sons.css';

function Sons() {
  const [genreTri, setGenreTri] = useState('');

  const trierParGenre = (sons, genre) => {
    return sons.filter((son) => son.genre.includes(genre));
  };

  const listeSonsTries = genreTri ? trierParGenre(listeSons, genreTri) : listeSons;

  return (
    <div>
      <div>
        Trier par genre:
        <select>
        <option onClick={() => setGenreTri('')}>Tous</option>
        <option onClick={() => setGenreTri('Gabber')}>Gabber</option>
        <option onClick={() => setGenreTri('Hardcore')}>Hardcore</option>
        <option onClick={() => setGenreTri('Tribe')}>Tribe</option>
        </select>
      </div>

      <div>
        {listeSonsTries.map((son) => (
          <div className="item">
            <img src={son.img} alt={son.titre} />
            <h2>{son.titre}</h2>
            {son.genre && <h3>{son.genre.join(' / ')}</h3>}
            <span>{son.prix}â‚¬</span>
          </div>
      ))}

      </div>
    </div>
  );
}

export default Sons;
