import './Panier.css';
// import logopanier from '.../Sons/icones/panier.png';

function Panier(props) {
  const [articles, setArticles] = useState([]);

  const ajouterArticle = (article) => {
    setArticles([...articles, article]);
  };

  return (
    <div className="panier-container">
      <div className="panier">
        {/* <img src={logopanier} alt="Panier" /> */}
        <span className="panier-count">{articles.length}</span>
      </div>
      <div className="panier-content">
        {articles.map((article) => (
          <div className="panier-article" key={article.titre}>
            <h3>{article.titre}</h3>
            <p>{article.prix}â‚¬</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Panier;
