import './Accueil.css'
import useLocoScroll from './hooks/useLocoScroll.js';
import Rotate from 'react-reveal';
import logo_rond from '../global/Nav/images/logo_rond.png';


import React, { useRef, useEffect, useState } from 'react';

function Accueil() {
    const [preloader, setPreloader] = useState(true);





    useEffect(() => {
        // Votre fonction ici
        console.log('La page a changÃ© !');
      }, []);


    
    
    useLocoScroll(!preloader);


    const [timer, setTimer] = useState(1);
    const id = useRef(null);

    const clear = ()=>{
        window.clearInterval(id.current);
        setPreloader(false);
    }

    useEffect(()=>{
        id.current = window.setInterval(()=>{
            setTimer((timer)=> timer-1)

        },1000 )

    }, [])

    useEffect(()=>{
        if(timer ===0){
            clear();
        }

    }, [timer])



    return(

        <span>
            {preloader?(
            
            <div className='loader'>
                <h1> chargement</h1>

            </div>)
            :(

            <div id="main-container" className="main-container" data-scroll-container> 
                <div className='div_b2b_records' data-scroll-section id="sec1">
                    <span className='span_b2b' >
                        <span className='titre_b2b' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                    </span>
                    <span className='span_records' >
                        <span className='titre_b2b' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="1">RECORDS</span>
                    </span>
                </div>



 
                <div className='section2' data-scroll-section id="sec2" >
                        <h1 className='label_ind blanc'  data-scroll-position="left" data-scroll data-scroll-direction="vertical" data-scroll-speed="-4">LABEL INDE</h1>      
                        <h1 className='label_ind'  data-scroll-position="left" data-scroll data-scroll-direction="vertical" data-scroll-speed="-1">NORMAND</h1>
                </div>
                
                <div className='section3' data-scroll-section id="sec3" >
                       <img className='logo_rond' data-scroll-target="#sec2" data-scroll-position="left" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-3" src={logo_rond}></img>
                </div>


                <section class="c-section" data-scroll-section>
                <div class="o-container" id="scroll-direction">
                    <div class="c-direction-block_wrapper">
                        <div class="c-section_infos -padding" data-scroll data-scroll-sticky data-scroll-target="#scroll-direction">
                            <div class="c-section_infos_inner" data-scroll>
                                <h3>
                                    02. 
                                    Scroll direction
                                </h3>
                                <div class="c-sections_infos_text u-text">
                                    <p>
                                        And if that wasn't enough, make 'em go backwards. Or upwards. Or downwards!
                                    </p>
                                </div>
                            </div>
                        </div>
                        <div class="c-direction-block" id="direction">
                            <div class="c-direction-block_item -one">
                                <span class="c-direction-block_item_inner" data-scroll data-scroll-direction="horizontal" data-scroll-speed="6" data-scroll-target="#direction">
                                    I'm moving in this direction
                                </span>
                            </div>
                            <div class="c-direction-block_item -two">
                                <span class="c-direction-block_item_inner" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-8" data-scroll-target="#direction">
                                    And in this direction
                                </span>
                            </div>
                            <div class="c-direction-block_item -three">
                                <span class="c-direction-block_item_inner" data-scroll data-scroll-direction="horizontal" data-scroll-speed="9" data-scroll-target="#direction" data-scroll-delay="0.05">
                                    Sooo customizable. Right?
                                </span>
                            </div>
                            <div class="c-direction-block_item -four">
                                <span class="c-direction-block_item_inner" data-scroll data-scroll-direction="horizontal" data-scroll-speed="15" data-scroll-target="#direction">
                                    I can also go in this direction
                                </span>
                            </div>
                            <div class="c-direction-block_item -five">
                                <span class="c-direction-block_item_inner" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-20" data-scroll-target="#direction" data-scroll-delay="0.05">
                                    Ok, enough!!!!
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            </section>






            </div>
            )}
        </span>

    )
}

export default Accueil;
