import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import './Formulaire.css';
import icoFleche from './ico/flecheretour.png';
import { Link } from 'react-router-dom';
import axios from "axios";

import Paiement from "./Paiement";


const schema = yup.object().shape({
  prenom: yup.string()
    .required('Veuillez fournir votre prénom.')
    .min(2, 'Le prénom doit comporter au moins 2 caractères.')
    .matches(/[aeiouyAEIOUY]/, 'Le prénom doit contenir au moins une voyelle.')
    .notOneOf([
      'firstname',
      'lastname',
      'unknown',
      'first_name',
      'last_name',
      'anonyme',
      'user',
      'admin',
      'name',
      'nom',
      'prénom',
      'test'
    ], 'Cette valeur est interdite.')
    .matches(/^[a-zA-Zéèêàâûù:'\-ç]+$/, 'Le prénom contient des caractères non autorisés.')
    .matches(/^(?!.*(.)\1{2}).*$/, 'La saisie contient des caractères répétitifs.')
    .test('not-equal', 'Le prénom ne peut pas être identique au nom.', function(value) {
      return value !== this.parent.nom;
    }),

  nom: yup.string()
    .required('Veuillez fournir votre nom.')
    .min(2, 'Le nom doit comporter au moins 2 caractères.')
    .matches(/[aeiouyAEIOUY]/, 'Le nom doit contenir au moins une voyelle.')
    .notOneOf([
      'firstname',
      'lastname',
      'unknown',
      'first_name',
      'last_name',
      'anonyme',
      'user',
      'admin',
      'name',
      'nom',
      'prénom',
      'test'
    ], 'Cette valeur est interdite.')
    .matches(/^[a-zA-Zéèêàâûù:'\-ç]+$/, 'Le nom contient des caractères non autorisés.')
    .matches(/^(?!.*(.)\1{2}).*$/, 'La saisie contient des caractères répétitifs.')
    .test('not-equal', 'Le nom ne peut pas être identique au prénom.', function(value) {
      return value !== this.parent.prenom;
    }),

  email: yup.string()
    .required('Veuillez fournir une adresse email.')
    .email('Veuillez fournir une adresse email valide.'),

  adresse: yup.string()
    .default('some string')
    .required('Veuillez fournir une adresse.'),

  zip: yup.number()
    .default('some string')
    .required('Veuillez fournir un code postal.')
    .typeError('Veuillez fournir un nombre.'),

  ville: yup.string()
    .default('some string')
    .required('Veuillez fournir une ville.')
});

function Formulaire({ panier, setvoirForm }) {
  function physique() {
    for (const prop in panier) {
      if (panier[prop].type !== "digital") {
        return true;
      }
    }
    return false;
  }


  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(schema)
  });


  const onSubmit = async (data) => {


    const paymentData = {
      checkout_reference: Date.now(),
      amount:  panier.reduce( (acc, article) => acc + article.nbr * article.prix, 0 ),
      currency: "EUR",
      pay_to_email: "bicraveurs2bass@gmail.com",
      description: "Test de paiement sum up",
      personal_details:{
        email: data.email,
        first_name: data.prenom,
        last_name :data.nom,
        adress :{
          country: "france",
          city: data.ville,
          postal_code: data.zip,
          line_1: data.adresse
        }
      }
    };
    try {
      const response = await axios.post('/checkout', paymentData);
        const url = 'https://api.sumup.com/v0.1/checkouts/'+ response.data.id;

        const data = {
          payment_type: 'card',
          card: {
            name: 'Charlie Beaufils',
            number: '5136415009212883',
            expiry_month: '00',
            expiry_year: '25',
            cvv: '027'
          }
        };
        
        const headers = {
          'Content-Type': 'application/json'
        };
        
        axios.put(url, data, { headers })
          .then(response2 => {
            console.log(response2.data);
            const nextStep = response2.data.next_step;
              if (nextStep && nextStep.method === 'POST' && nextStep.mechanism.includes('iframe')) {
                const iframeUrl = nextStep.url;
                const payload = nextStep.payload;

                // Créer dynamiquement un iframe
                const iframe = document.createElement('iframe');
                iframe.src = iframeUrl;
                iframe.style.width = '100%';
                iframe.style.height = '100%';
                // Ajouter le contenu du payload en tant que paramètres de requête à l'URL du iframe
                iframe.src += '?' + new URLSearchParams(payload).toString();

                // Ajouter l'iframe à votre page HTML pour l'afficher
                document.body.appendChild(iframe);
              } else {
                console.log('Next step is not supported or requires a different method.');
              }

          })

          .catch(error => {
            console.error(error);
          });




      // Traitez la réponse de l'API SumUp ici
    } catch (error) {
      console.error(error);
      // Gérez les erreurs ici
    }
  }
  


  


  

  return (
    <div className='div_form'>
      <form className="form" onSubmit={handleSubmit(onSubmit)}>
        <img onClick={()=> setvoirForm(false)} src={icoFleche} className="icoFleche" alt="Retour au panier"/>
        <span className="titre_infos">Vos infos</span>
        <span className="paireForm">
          <input {...register("prenom")} placeholder="Prénom" />
          {errors.prenom && <span>{errors.prenom.message}</span>}
          <input className={"paire"} {...register("nom")} placeholder="Nom" />
          {errors.nom && <span>{errors.nom.message}</span>}
        </span>
        <input {...register("email")} placeholder="Email" />
        {errors.email && <span>{errors.email.message}</span>}
        <span className="span_physique">
          <input {...register("adresse")} placeholder="Adresse" />
          {errors.adresse && <span>{errors.adresse.message}</span>}
          <span className="paireForm">
            <input {...register("zip")} placeholder="Code postal" />
            {errors.zip && <span>{errors.zip.message}</span>}
            <input className="paire" {...register("ville")} placeholder="Ville" />
            {errors.ville && <span>{errors.ville.message}</span>}
          </span>
          <input disabled="disabled" placeholder="France only !" />
        </span>
        <input className="btnValider" type="submit" value='valider' />
      </form>
    </div>
  );}

export default Formulaire;
