import React, { useState } from 'react';
import './Panier.css';
import icoClose from './ico/close.png';



function Panier({ panier, updatePanier, voirPanier, setvoirPanier }) {
	const totalMontant = panier.reduce(
		(acc, article) => acc + article.nbr * article.prix,
		0
	)
    const totalArticle = panier.reduce(
		(acc, article) => acc +  parseInt(article.nbr)  ,
		0
	)
function supprimerDuPanier(titre, nbr){ 
  const nouveauPanier = panier.map((article) => {
    if (article.titre === titre) {
      return {
        ...article,
        nbr:0,
        prix: article.prix
      };
    }
    return article;
  });
  updatePanier(nouveauPanier);
  console.log(nouveauPanier);
}

function viderPanier(){ 
  updatePanier([]);
}

function  modifierValue(titre, nbr, value, type){
  const nouveauPanier = panier.map((article) => {
    if (article.titre === titre) {
      console.log(value);
      if (article.type === "digital"){
        if(value >1){
          alert("impossible");
          return {  
            ...article,
            nbr:"1",
            prix: article.prix
          }
        }
        else{
          return {  
            ...article,
            nbr: 0,
            prix: article.prix
          }
        }
      }
      else{
        return {
          ...article,
          nbr:value,
          prix: article.prix
        }
      }
    }
    return article;
  });
  console.log(nouveauPanier);
  updatePanier(nouveauPanier);
}


function classChanger(id_target, add , remove){
  if (document.getElementById(id_target) != null){
    let target = document.getElementById(id_target);
    target.classList.add(add);
    target.classList.remove(remove);
    console.log("test")
  }
}

console.log(voirPanier);

if (voirPanier ===true){
  console.log("panier vu");
  classChanger('panier' , 'container_panier--active', 'container_panier--disabled' );
}
else{
  classChanger('panier' , 'container_panier--disabled', 'container_panier--active' );
}


    return (

      <div id="panier" className="container_panier container_panier--disabled">
          <span>Panier</span>
          {panier.map((article, index) => (
            <div key={index} className='div_items'>
              {article.nbr>0 && 
              <div>
              <span>{article.titre}</span>
              <span>{article.prix}</span>
              <span> {article.nbr}</span>
              {/* <h2>{article.type}</h2> */}
              <button onClick={() => supprimerDuPanier(article.titre, article.nbr)}>Supprimer du Panier</button>
              <input type="number" className="nbrValue" value={article.nbr} onChange={e => modifierValue(article.titre, article.nbr, e.target.value, article.type) }
/>

              </div>           
              }
            </div>
            
          ))}
          <img src={icoClose} className='boutonClose' onClick={() => setvoirPanier(false)}></img>
          {totalArticle<1 && <span>C'est bien vide par ici!</span>}
          {totalMontant>0 && 
          <div>
            <h1>Total:{totalMontant} €</h1>
            <h1>Total articles:{totalArticle} </h1> 
            <button onClick={()=>viderPanier()}> Vider le panier</button>  
          </div>}                     
        </div>

      );
    
}

export default Panier;
