import React from 'react';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

function Paiement({prix, infos}) {
  const handleToken = async (token, adresses) => {
    const res = await axios.post("http://localhost:3001/paiement", {
      token
    });
    const { status } = res.data;
    if (status === "success") {
      console.log("Success ! Check emails for details", {
        type: "success"
      });
    } else {
      console.log("Something went wrong", {
        type: "failure"
      });
    }
  };





  return (
    <StripeCheckout
      email={infos.email}
      token={handleToken}
      stripeKey="pk_test_51N7c9qExy3d78d8RDIO7C4yybFGxEHGFuGjY7mHd0LUPZ8iCpQkO64C87nTa9AjK2Br6oHsbMGSuQPx4FA5AsCHf00BMo9cTVN"
      amount='1000' // Montant en centimes (1000 = 10.00 €)
      name="Boutique Bicraveurs2Bass"
      description="Paiement sécurisé par Stripe"
      locale="fr"
      currency='EUR'
    >
    <button  className="btnValider" type="submit" value='valider' >Payer maintenant</button>
    </StripeCheckout>
    
  );
};

export default Paiement;




