import React, { useState } from 'react';
import './App.css';

import AfficherSons from './Sons/Afficher_sons.js'; 
import Panier from './Panier/Panier.js'; 
import Nav from './Nav/Nav.js'; 
import Curseur from './Curseur/Curseur.js'; 
import Player from './Player/Player.js'; 
import IcoPanier from './IcoPanier/IcoPanier.js'; 
import AfficherPhysique from './AfficherPhysique/AfficherPhysique.js';

import totalArticle from './Panier/Panier.js';
import classChanger from './IcoPanier/IcoPanier.js';

function App() {
  const [panier, updatePanier] = useState([])
  const [player, updatePlayer] = useState('')
  const [isPlaying, setIsPlaying] = useState(false);
  const [voirPanier, setvoirPanier] = useState(false);

  return (
    <div className="App">
      <div id="flou" className="--flou_disabled">
        <Curseur/>
        <Nav/>
        <AfficherPhysique panier={panier} updatePanier={updatePanier}/>
        <AfficherSons panier={panier} updatePanier={updatePanier} updatePlayer={updatePlayer} isPlaying={isPlaying} setIsPlaying={setIsPlaying}/>
        <IcoPanier totalArticle ={ totalArticle } panier={panier} isPlaying={isPlaying} voirPanier={voirPanier} setvoirPanier={setvoirPanier}/>
        <Player url={player} isPlaying={isPlaying} setIsPlaying={setIsPlaying} allow='autoplay; encrypted-media'  />
      </div >

      <Panier panier={panier} updatePanier={updatePanier} voirPanier={voirPanier} setvoirPanier={setvoirPanier}/>
    </div>

  );
}

export default App;
export { totalArticle };
