import React from 'react';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const Paiement = (prix) => {
  const handleToken = async (token) => {
    try {
      const response = await axios.post('localhost:3001/paiement', {
        token: token,
        amount: 1000, // Montant en centimes (1000 = 10.00 €)
      });
      
      console.log(response.data.message); // Affichez le message de succès dans la console ou utilisez-le selon vos besoins

      // Effectuez ici les actions nécessaires après le paiement réussi, comme vider le panier, rediriger vers une page de confirmation, etc.
    } catch (error) {
      console.error(error.response.data.error); // Affichez l'erreur dans la console ou traitez-la selon vos besoins
    }
  };
  
  return (
    <StripeCheckout
      token={handleToken}
      stripeKey="pk_test_51N7c9qExy3d78d8RDIO7C4yybFGxEHGFuGjY7mHd0LUPZ8iCpQkO64C87nTa9AjK2Br6oHsbMGSuQPx4FA5AsCHf00BMo9cTVN"
      amount={prix} // Montant en centimes (1000 = 10.00 €)
      name="Nom de votre boutique"
      description="Description de l'article"
      locale="fr"
    >
    <button type="button">Payer maintenant</button>
    </StripeCheckout>
    
  );
};

export default Paiement;
