import React, { useState } from 'react';
import ReactPlayer from 'react-player/soundcloud'

function Player({ url }) {

      <div className="div_player">
        <ReactPlayer
          url={url}
          playing={true}
          width="100%"
          height="10vw"
          onPlay={() => setIsPlaying(true)}
          onPause={() => {
            setIsPlaying(false);
            setShowPlayer(false); // cache le lecteur audio lorsque l'utilisateur met en pause
          }}
        />
      </div>
}

export default Player;
