import React, { useState } from 'react';
import ReactPlayer from 'react-player/soundcloud'

function Player({ url, isPlaying}) { 

    if (isPlaying){  
    return (
      <div className="div_player">
          <ReactPlayer 
          url={url}   
          playing={true} 
          width="100%" 
          height="10vw"
          onPlay={() => isPlaying(true)}
          onPause={() => isPlaying(false)}
          />
      </div>
    );
    }
}


export default Player;
