import './Accueil.css'
import useLocoScroll from './hooks/useLocoScroll.js';



import React, { useRef, useEffect, useState } from 'react';

function Accueil() {
    const [preloader, setPreloader] = useState(true);

    useLocoScroll(!preloader);

    
    const [timer, setTimer] = useState(3);
    const id = useRef(null);

    const clear = ()=>{
        window.clearInterval(id.current);
        setPreloader(false);
    }

    useEffect(()=>{
        id.current = window.setInterval(()=>{
            setTimer((timer)=> timer-1)

        },1000 )

    }, [])

    useEffect(()=>{
        if(timer ===0){
            clear();
        }

    }, [timer])




    return(

        <span>
            {preloader?(
            
            <div className='loader'>
                <h1> chargement</h1>

            </div>)
            :(

            <div id="main-container" data-scroll-container> 
                <div className='div_b2b_records' data-scroll-section >
                    <span className='span_b2b' >
                        <span className='titre_b2b' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="-1">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="0">BICRAVEURS2BASS</span>
                        <span className='titre_b2b titre_b2b--contour' data-scroll-position="top" data-scroll data-scroll-direction="horizontal" data-scroll-speed="2">BICRAVEURS2BASS</span>
                    </span>

                    <span className='span_records' >
                        <span className='titre_b2b'>RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour'>RECORDS</span>
                        <span className='titre_b2b titre_b2b--contour'>RECORDS</span>
                    </span>

                </div>
            </div>
            )}
        </span>

    )
}

export default Accueil;
