import React, { useState } from 'react';
import axios from 'axios';

function Formulaire() {
  const [state, setState] = useState({
    address: '',
    city: '',
    postcode: '',
    error: null
  });

  const handleSubmit = async event => {
    event.preventDefault();

    try {
      const response = await axios.get(`https://api.laposte.fr/datalab/v1/adresse?q=${state.address}&type=${state.type}&limit=${state.limit}`, {
        headers: {
          'X-Okapi-Key': 'your_api_key_here',
          'Accept': 'application/json'
        }
      });

      const data = response.data;
      const results = data.adresses;

      if (results.length > 0) {
        const firstResult = results[0];
        const city = firstResult.localite;
        const postcode = firstResult.codePostal;
        setState({ ...state, city, postcode, error: null });
      } else {
        setState({ ...state, error: 'No results found' });
      }
    } catch (error) {
      setState({ ...state, error: 'An error occurred' });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Address:
        <input type="text" value={state.address} onChange={event => setState({ ...state, address: event.target.value })} />
      </label>
      <br />
      <label>
        Type:
        <input type="text" value={state.type} onChange={event => setState({ ...state, type: event.target.value })} />
      </label>
      <br />
      <label>
        Limit:
        <input type="number" value={state.limit} onChange={event => setState({ ...state, limit: event.target.value })} />
      </label>
      <br />
      <button type="submit">Submit</button>
      {state.error && <p>{state.error}</p>}
      {state.city && <p>City: {state.city}</p>}
      {state.postcode && <p>Postcode: {state.postcode}</p>}
    </form>
  );
}
export default Formulaire;
