import './CarteArtiste.css'

import img_lichar from './images_dj/legroslichar.png';
import img_pomox from './images_dj/legrospomox.png';




 function CarteArtiste({nom, desc, speed}){
    function img(nom){
        if (nom==="Lich'ar"){
            return(img_lichar);
        }

        if (nom==="Pomox"){
            return(img_pomox);
        }
    }

    return(
        <div className='carte_artiste' style={{backgroundImage: `url(${img(nom)})`}}  data-scroll-position="top"  data-scroll data scroll sticky data-scroll-direction="horizontal" data-scroll-speed={speed}>
            <h3 className='nom_artiste'>{nom}</h3>
            <p>{desc}</p>
            <img className='img_artiste' alt={"photo du DJ " +nom}></img>

        </div>
    )


}

export default CarteArtiste;
