import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { useEffect } from 'react';
import './Nav.css';
import logo_rond from './/images/logo_rond.png';
import home from './ico/home.png';
import musique from './ico/musique.png';
import shop from './ico/shop.png';
import calendrier from './ico/calendrier.png';

import InvitClick from './invitClick/invitClick';

function useScrollPosition() {
    const [scrollPosition, setScrollPosition] = useState(0);
  
    useEffect(() => {
      const handleScroll = () => {
        const position = window.pageYOffset || document.documentElement.scrollTop;
        setScrollPosition(position);
      };
  
      window.addEventListener('scroll', handleScroll);
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);
  
    return scrollPosition;
  }




function Nav() {
    const scrollPosition = useScrollPosition();
    const location = useLocation();    
 
    return (

        <nav data-scroll-section>
            { (location.pathname === "/paiement/success" || (location.pathname === "/" && scrollPosition < 10 ))  &&<InvitClick/>}
        <div class="bloc_Nav">    
            <NavLink to="/">
            <img class="icones_Nav" src={home}/>
            </NavLink>
            <NavLink to="/musique">
            <img class="icones_Nav" src={musique}/>
            </NavLink>

            <NavLink to="/shop">
            <img class="icones_Nav" src={shop}/>
            </NavLink>

            <NavLink to="/events">
            <img class="icones_Nav" src={calendrier}/>
            </NavLink>



            <img class="logo_Rond_nav" src={logo_rond}/>

            <div class="nav_full_width">

                
            <div class="div_titres">




            <NavLink
                id="navLink"
                to="/"
                className={({ isActive, isPending }) =>
                     isActive ? "active" : "pending"
                }
                >
                Accueil
                </NavLink>

 
            <NavLink
                id="navLink"
                to="/musique"
                className={({ isActive, isPending }) =>
                     isActive ? "active" : "pending"
                }
                >
                Musique
                </NavLink>


                <NavLink
                id="navLink"
                to="/shop"
                className={({ isActive, isPending }) =>
                     isActive ? "active" : "pending"
                }
                >
                Boutique
                </NavLink>


                <NavLink
                id="navLink"
                to="/events"
                className={({ isActive, isPending }) =>
                     isActive ? "active" : "pending"
                }
                >
                Events
                </NavLink>
 

            </div>
            </div>

        </div>
        </nav>

      );
    
}
export default Nav;
