import React, { useState } from 'react';

function Formulaire() {
  const [state, setState] = useState({
    numero_rue: '',
    type_voie: '',
    nom_voie: '',
    code_postal: '',
    ville: '',
    error: null
  });

  const handleSubmit = async event => {
    event.preventDefault();
    // Envoyer la requête à l'API de La Poste ici
  };

  const handleChange = event => {
    setState({ ...state, [event.target.name]: event.target.value });
  };

  return (
    <div className='div_form'>

<form onSubmit={handleSubmit}>
      <label>
        Address:
        <input type="text" value={state.address} onChange={event => setState({ ...state, address: event.target.value })} />
      </label>
      <br />
      <label>
        Type:
        <input type="text" value={state.type} onChange={event => setState({ ...state, type: event.target.value })} />
      </label>
      <br />
      <label>
        Limit:
        <input type="number" value={state.limit} onChange={event => setState({ ...state, limit: event.target.value })} />
      </label>
      <br />
      <button type="submit">Submit</button>
      {state.error && <p>{state.error}</p>}
      {state.city && <p>City: {state.city}</p>}
      {state.postcode && <p>Postcode: {state.postcode}</p>}
