import { useStripe, useElements, CardElement } from 'react-stripe-elements';

const Paiement = () => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { token } = await stripe.createToken(elements.getElement(CardElement));
    
    if (token) {
      // Envoyez le token au backend pour finaliser le paiement
      const response = await fetch('/paiement', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: token.id }),
      });

      if (response.ok) {
        // Redirigez l'utilisateur vers la page de succès du paiement
        window.location.href = '/paiement/succes';
      } else {
        // Gérez les erreurs de paiement
        console.error('Erreur de paiement');
      }
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit">Payer</button>
    </form>
  );
}

export default Paiement;
