import React from 'react';
import StripeCheckout from 'react-stripe-checkout';

const Paiement = () => {
  const handlePayment = async (token) => {
    try {
      const response = await fetch('/paiement', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: token.id }),
      });

      if (response.ok) {
        // Redirigez l'utilisateur vers la page de succès du paiement
        window.location.href = '/paiement/succes';
      } else {
        console.error('Erreur de paiement');
      }
    } catch (error) {
      console.error('Erreur de paiement', error);
    }
  };

  return (
    <StripeCheckout
      token={handlePayment}
      stripeKey="pk_live_51N7c9qExy3d78d8RirH1wR9yYPpRxDgjXrwE1wKaWTDkdFMUzhkYGR4f6nLRopOR8QZDuQSpOCNQTJRC0aIBwQb300wvnOnuAB"
      amount={50} // Montant en centimes (10 €)
      currency="EUR"
      name="Nom du produit"
      billingAddress
    >
      <button type="button">Payer</button>
    </StripeCheckout>
  );
};

export default Paiement;
