import React, { useState } from 'react';

import './Panier.css';




function Panier({ panier, updatePanier }) {


	const total = panier.reduce(
		(acc, article) => acc + article.nbr * article.prix,
		0
	)


    return (
        <div className="container_panier">
          {panier.map((article, index) => (
            <div key={index}>
              {article.titre}  {article.prix}€    Quantité {article.nbr}


            </div>
            
          ))}
          {

                <h1>Total:{total > 0 ,  total}</h1>

          }
                       
        </div>
      );
    
}

export default Panier;
