import Mainshop from './boutique/Mainshop.js'



function App() {
  const [panier, updatePanier] = useState([])
  const [player, updatePlayer] = useState('')
  const [isPlaying, setIsPlaying] = useState(false);
  const [voirPanier, setvoirPanier] = useState(true);

  return (


    <div className="App">
        <meta name="viewport" content="width=device-width, initial-scale=1.0, shrink-to-fit=no"></meta>

        <Routes>
          <Route path="/shop" element={ <Mainshop/>}>

            <Route path="/shop/panier" element={ <Panier panier={panier} updatePanier={updatePanier} voirPanier={voirPanier} setvoirPanier={setvoirPanier} /> }></Route>
          </Route>

        </Routes>

    </div>

  );
}

export default App;
