import React, { useState } from 'react';
import {Routes, Route} from 'react-router-dom';
import Panier from './Panier/Panier.js';


function Mainshop() {
    const [panier, updatePanier] = useState([])
    const [voirPanier, setvoirPanier] = useState(false);

  return (
    

    <div className="App">

    <Routes>
        <Route path="/shop" element={<Mainshop/>} >
            <Route path="/shop/panier" element={<Panier panier={panier} updatePanier={updatePanier} voirPanier={voirPanier} setvoirPanier={setvoirPanier}/>} />

        </Route>
  
    </Routes>

      

    </div>
    
  );
}

export default Mainshop;
