import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import './Nav.css';
import logo_rond from './/images/logo_rond.png';
import home from './ico/home.png';
import musique from './ico/musique.png';
import shop from './ico/shop.png';
import calendrier from './ico/calendrier.png';

function Nav() {

    return (

        <nav>
        <div class="bloc_Nav">    
            <img class="icones_Nav" src={home}/>
            <img class="icones_Nav" src={musique}/>
            <img class="icones_Nav" src={shop}/>
            <img class="icones_Nav" src={calendrier}/>

            <img class="logo_Rond_nav" src={logo_rond}/>
            <div class="nav_full_width">
            <div class="div_titres">
                <div class="div_titresNav active">
                    <span class="nav_Fullwidth nav_Fullwidth--isactive">Accueil</span>
                </div>
                <NavLink
                    to="/shop"
                    className="div_titresNav"
                    style={({ isActive, isPending }) => {
                    return {
                        fontWeight: isActive ? "bold" : "",
                        color: isPending ? "red" : "black",
                        backgroundColor: isPending ? "black" : "white",
                        };
                    }}
                >
  Boutique
</NavLink>

                <div class="div_titresNav">
                    <span class="nav_Fullwidth">Musique</span>
                </div>
 

                <div class="div_titresNav">
                    <span class="nav_Fullwidth">Events</span>
                </div> 
            </div>
            </div>

        </div>
        </nav>

      );
    
}
export default Nav;
